mapper.update:
update hyj_admin
set password = #{newPassword}, update_time = #{updateTime}
where del_flag = 0
  and email = #{email}

update hyj_admin

    update_time = #{updateTime},

        email = #{email},


        `name` = #{name}


where id = #{id}

update hyj_admin
set del_flag    = 1,
    update_time = #{updateTime}
where id = #{adminId}

update hyj_admin
set status      = #{status},
    update_time = #{updateTime}
where id = #{adminId}

mapper.select:
select count(*)
from hyj_admin


        email = #{email}


        or name = #{name}

    and (id != #{id} and del_flag = 0)
